mapper.update:
update a_posts
set view_count = view_count + 1
where id = #{id}

update a_posts
set state = 'N'
where id = #{id}

mapper.select:
select count(*)
from a_post_likes
where post_id = #{postId}
  and user_id = #{userId}

mapper.insert:
insert into a_post_likes (id, post_id, user_id)
values (
  (select nvl(max(id), 0) + 1 from a_post_likes),
  #{postId},
  #{userId}
)

mapper.delete:
delete from a_post_likes
where post_id = #{postId}
  and user_id = #{userId}
