mapper.select:
select * from (
  select inner.*, rownum as rn
  from (
    select * from a_posts
    where user_id = #{id}
      and state = 'Y'
    order by id desc
  ) inner
  where rownum <= #{endno}
)
where rn >= #{startno}

select * 
from (
  select inner.*, rownum as rn
  from (
    select c.*, p.title as post_title, p.content as post_content
    from a_comments c
    join a_posts p on c.post_id = p.id
    where c.user_id = #{id} 
      and c.state = 'Y'
    order by c.post_id desc
  ) inner
  where rownum <= #{endno}
)
where rn  >= #{startno}

SELECT * FROM (
  SELECT inner.*, rownum AS rn
  FROM (
    SELECT * FROM a_posts
    WHERE user_id = #{id}
      AND state = 'Y'
      AND (title LIKE '%' || #{search} || '%' OR content LIKE '%' || #{search} || '%')
    ORDER BY id DESC
  ) inner
  WHERE rownum <= #{endno}
)
WHERE rn >= #{startno}

select * 
from (
  select inner.*, rownum as rn
  from (
    select c.*, p.title as post_title, p.content as post_content
    from a_comments c
    join a_posts p on c.post_id = p.id
    where c.user_id = #{id}
      and c.state = 'Y'
      and c.content like '%' || #{search} || '%'
    order by c.post_id desc
  ) inner
  where rownum <= #{endno}
)
where rn >= #{startno}

mapper.update:
update a_users
set phone = #{phone}, nickname = #{nickname}
where id = #{id}

update a_users
set state = 'N'
where id = #{id}
